import * as api from '../api';

//Action Creators
export const getPosts = () => async (dispatch) => {
    try {
        const { data } = await 'http//localhost:5000/posts';
        console.log(data);
        dispatch({ type: 'FETCH_ALL', payload: data});
    }
    catch (err) {
        console.log(err.message)
    }  
}
