import React, { useState, useEffect } from 'react';
import useStyles from './styles';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import FileBase from 'react-file-base64';
import { useDispatch, useSelector } from 'react-redux';
import { createPost, updatePost, deleteAllPosts } from '../../actions/posts';

const Form = ( {currentId, setCurrentId} ) => {
   const [postData, setPostData] = useState({
     creator: '',
     title:'',
     message:'',
     tags:'',
     selectedFile: ''
   });
   const post = useSelector((state) => currentId ? state.posts.find((p) => p._id === currentId) : null);
   const classes = useStyles();
   const dispatch = useDispatch();

   useEffect(() => {
     if(post) setPostData(post)
   }, [post])

   const handleSubmit = (e) => {
    e.preventDefault();
    console.log('this is the post data -->', postData);
    console.log('this is currentId -->', currentId);
    //simple validator of data entry
    if(currentId) {
      console.log('creating Update');
      dispatch(updatePost(currentId, postData));

    } else {
      if(postData.creator === '' || postData.title === '' || postData.message === '') {
        return alert('Creator, title and message fields are required');
      }
      console.log('creating Post');
      dispatch(createPost(postData));
    }
   }

   const clear = () => {
    setCurrentId(null)
    setPostData({
      creator: '',
      title:'',
      message:'',
      tags:'',
      selectedFile: ''
    });
   }

   const destroyMemories = () => {
     var result = window.confirm("Are you sure you want to delete all memories?");
     if (result){
       console.log('destroyed all memories');
       dispatch(deleteAllPosts());
     }
   }
     return (
      <Paper className={classes.paper}>
        <form autoComplete='off' noValidate 
        className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
          <Typography variant='h6'>{currentId ? 'Editing your Nostalgia' : 'Creating Nostalgia'}</Typography>
          <TextField required name="creator" variant="outlined" label="Creator" fullWidth
          value={postData.creator}
          onChange={(e) => setPostData({...postData, creator: e.target.value })}/>
          <TextField required name="title" variant="outlined" label="title" fullWidth
          value={postData.title}
          onChange={(e) => setPostData({...postData, title: e.target.value })}/>
          <TextField required name="message" variant="outlined" label="message" fullWidth
          value={postData.message}
          onChange={(e) => setPostData({...postData, message: e.target.value })}/>
          <TextField name="tags" variant="outlined" label="tags" fullWidth
          value={postData.tags}
          onChange={(e) => setPostData({...postData, tags: e.target.value.split(',') })}/>
          <div className={classes.fileInput}>
            <FileBase type='file' multiple={false} 
            onDone={({base64}) => setPostData({...postData, selectedFile: base64})}>
            </FileBase>
          </div>
          <Button className={classes.buttonSubmit} variant='contained' color='primary' size='large' type='submit' fullWidth>
            Submit
          </Button>
          <Button className={classes.clearButton} variant='contained' color='secondary' size='small' onClick={clear} fullWidth>
            clear
          </Button>
          <Button variant='contained' color='default' size='large' onClick={destroyMemories} fullWidth>
            DESTROY MEMORIES
          </Button>
        </form>
      </Paper>
    )
}

export default Form;
