export default (posts = [], action) => {
    switch (action.type) {
        case 'FETCH_ALL':
            //return action posts
            return action.payload;
        case 'CREATE':
            return [...posts, action.payload];
        case 'UPDATE':
            return posts.map((post) => post_id === action.payload._id ? action.payload : post)
        case 'DELETE_ALL':
            return [];
        default:
            return posts;
    }   
}
