export default (posts = [], action) => {
    //true logic soon
    switch (action.type) {
        case 'FETCH_ALL':
            //return action posts
            return action.payload;
        case 'CREATE':
            return [...posts, action.payload];
        case 'DELETE_ALL':
            return [];
        default:
            return posts;
    }   
}
