import React, { useState, useEffect } from 'react';
import { Container, AppBar, Typography, Grow, Grid } from '@material-ui/core';
import  { useDispatch } from 'react-redux';

import { getPosts } from './actions/posts';
import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import memorabilia from './images/memorabilia.jpeg';
import useStyles from './styles';

const App = () => {
    const [currentId, setCurrentId] = useState(0); 
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getPosts());
    }, [currentId, dispatch]);

    return (
       <Container maxWidth = 'lg'>
           <AppBar className={classes.appBar} position='static' color='inherit'>
               <Typography className={classes.heading} variant = 'h2' align = 'center' height = "60" >Melancholia</Typography>
               <img className={classes.image} src={memorabilia} alt='melancholia' height='60'></img>
           </AppBar>
           <Grow in>
             <Container>
                 <Grid className={classes.mainContainer} container justify="space-between" alignItems="stretch" spacing={3} >
                    <Grid item xs={12} sm={7}>
                        <Posts setCurrentId={setCurrentId}/>
                    </Grid>
                    <Grid item xs={12} sm={4}>
                        <Form currentId={currentId} setCurrentId={setCurrentId} />
                    </Grid>
                 </Grid>
             </Container>
           </Grow>
       </Container>
    )
}

export default App;
